	@Test
	public void testSegmentDocument() throws SimpleMarshallerException,
			SAXException, IOException {
		// Arrange
		// Request parameters: original C32
		final String c32Xml = ORIGINAL_C32_XML;
		// Request parameters: enforcement policies
		final XacmlResult xacmlResult = new XacmlResult();
		xacmlResult.setMessageId(UUID.randomUUID().toString());
		xacmlResult.setPatientId("d3bb3930-7241-11e3-b4f7-00155d3a2124");
		xacmlResult.setPdpDecision("Permit");
		final List<String> obligations = new LinkedList<String>();
		obligations.add("HIV");
		obligations.add("10160-0");
		xacmlResult.setPdpObligations(obligations);
		xacmlResult
				.setSubjectPurposeOfUse(SubjectPurposeOfUse.HEALTHCARE_TREATMENT);
		final String enforcementPolicies = marshaller.marshal(xacmlResult);
		final DSSRequest request = new DSSRequest();
		request.setDocumentXml(c32Xml);
		request.setEnforcementPoliciesXml(enforcementPolicies);

		// Act
		final DSSResponse response = sut.segmentDocument(request);

		// Assert
		final List<String> ignoreList = new LinkedList<String>();
		ignoreList.add("effectiveTime");
		final DetailedDiff diff = XmlComparator.compareXMLs(
				EXPECTED_SEGMENTED_C32_XML, response.getSegmentedDocumentXml(),
				ignoreList);
		assertTrue(diff.similar());
	}

